import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router'; 
import { AuthService } from 'src/app/services/auth/auth.service';


@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent implements OnInit {
  loginForm!: FormGroup;

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private router: Router
  ) { }

  ngOnInit(): void {
    this.loginForm = this.fb.group({
      username: ['', Validators.required],
      password: ['', Validators.required]
    });
  }

  onLogin(): void {
    if (this.loginForm.valid) {
      const { username, password } = this.loginForm.value;
      this.authService.login(username, password).subscribe({
        next: (res) => {
          this.router.navigate(['/devices']); 
        },
        error: (err) => {
          console.error('Erro na autenticação', err);
        }
      });
    }
  }
}
<div class="container-fluid align-self-center">
<form [formGroup]="loginForm" (ngSubmit)="onLogin()" class="login-container">
  <div class="text-center mb-4">
    <img src="caminho-para-logo.png" alt="Logo da Empresa" class="login-logo">
  </div>

  <div class="mb-3">
    <label for="username" class="form-label">Username</label>
    <input type="text" id="username" formControlName="username" class="form-control" placeholder="Enter your username">
  </div>

  <div class="mb-3">
    <label for="password" class="form-label">Password</label>
    <input type="password" id="password" formControlName="password" class="form-control" placeholder="Enter your password">
  </div>

  <div class="d-grid gap-2">
    <button type="submit" class="btn btn-block btn-primary" [disabled]="loginForm.invalid">Login</button>
  </div>
</form>
</div>
