import { Component, EventEmitter, Input, OnInit, Output, SimpleChanges } from '@angular/core';
import { Device } from 'src/app/models/device.model';
import { DeviceService } from 'src/app/services/device/device.service';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-device-list',
  templateUrl: './device-list.component.html',
  styleUrls: ['./device-list.component.scss']
})
export class DeviceListComponent implements OnInit {
  devices: Device[] = [];
  @Output() deviceToEdit = new EventEmitter<Device>();
  selectedDevice: Device | null = null;

  @Input() refresh: boolean | undefined;

  constructor(private deviceService: DeviceService) { }

  ngOnInit(): void {
    this.getDevices();
  }

  ngOnChanges(changes: SimpleChanges): void {
    if (this.refresh) {
      console.log('Atualizando lista de dispositivos');
      this.getDevices();
    }
  }


  getDevices(): void {
    this.deviceService.getDevices().subscribe({
      next: (devices) => {
        this.devices = devices;
      },
      error: (error) => {
        console.error('Erro ao obter dispositivos', error);
      }
    });
  }
  editDevice(device: Device): void {
    this.selectedDevice = device;
    this.deviceToEdit.emit(device);
  }

  deleteDevice(device: Device): void {
    Swal.fire({
      title: 'Tem certeza?',
      text: "Você não poderá reverter isso!",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Sim, delete!'
    }).then((result) => {
      if (result.isConfirmed) {
        this.deviceService.deleteDevice(device.id).subscribe({
          next: () => {
            this.getDevices();
            Swal.fire('Deletado!', 'O dispositivo foi deletado.', 'success');
          },
          error: (error) => {
            Swal.fire('Erro!', 'Não foi possível deletar o dispositivo.', 'error');
          }
        });
      }
    });
  }
}
<div class="container-fluid shadow-sm mt-5">

  <h3 class="title">Lista de dispositivos cadastrados</h3>
  <hr class="mb-4">

    <table class="table table-hover shadow-sm">
      <thead class="bg-light text-primary">
        <tr>
          <th scope="col" class="col-1"></th>
          <th scope="col" class="col-lg-3 col">Nome</th>
          <th scope="col" class="col-lg-3 col ">Fabricante</th>
          <th scope="col" class="col-3">Descrição</th>
          <th scope="col" class="col-lg-2 col text-right">Ações</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let device of devices">
          <td><i class="fa-solid fa-microchip"></i></td>
          <td>{{ device.name }}</td>
          <td>{{ device.manufacturer }}</td>
          <td  class="col-sm-3">{{ device.description }}</td>
          <td class="text-right col-5 col-md-3">
            <button class="btn btn-outline-info btn-sm" (click)="editDevice(device)">
              <i class="fas fa-edit"></i>
            </button>
            <button class="btn btn-outline-danger btn-sm" (click)="deleteDevice(device)">
              <i class="fas fa-trash-alt"></i>
            </button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
  <ng-template #noDevices>
    <div class="alert alert-info text-center">Nenhum dispositivo encontrado. Adicione um novo device acima!</div>
  </ng-template>
