import { Component, OnInit, Input } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { DeviceService } from 'src/app/services/device/device.service';
import { Device } from 'src/app/models/device.model';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-device-register',
  templateUrl: './device-register.component.html',
  styleUrls: ['./device-register.component.scss']
})
export class DeviceRegisterComponent implements OnInit {
  deviceForm: FormGroup;
  @Input() editingDevice: Device | null = null; // Use esta linha para receber o dispositivo para edição

  constructor(
    private fb: FormBuilder,
    private deviceService: DeviceService
  ) {
    this.deviceForm = this.fb.group({
      name: ['', Validators.required],
      description: ['', Validators.required],
      manufacturer: ['', Validators.required]
    });
  }

  ngOnInit(): void {
    if (this.editingDevice) {
      // Se um dispositivo para edição foi fornecido, preencha o formulário com seus dados
      this.deviceForm.setValue({
        name: this.editingDevice.name,
        description: this.editingDevice.description,
        manufacturer: this.editingDevice.manufacturer
      });
    }
  }

  submitForm(): void {
    if (this.deviceForm.valid) {
      const deviceData = this.deviceForm.value;
      if (this.editingDevice) {
        // Lógica para atualizar um dispositivo existente
        this.deviceService.updateDevice({ ...deviceData, id: this.editingDevice.id }).subscribe({
          next: () => {
            Swal.fire('Sucesso', 'Dispositivo atualizado com sucesso!', 'success');
            this.deviceForm.reset();
            this.editingDevice = null; // Reseta a variável após a atualização
            // Adicione aqui a lógica para atualizar a lista de dispositivos, se necessário
          },
          error: () => Swal.fire('Erro', 'Erro ao atualizar o dispositivo!', 'error')
        });
      } else {
        // Lógica para adicionar um novo dispositivo
        this.deviceService.addDevice(deviceData).subscribe({
          next: () => {
            Swal.fire('Sucesso', 'Dispositivo registrado com sucesso!', 'success');
            this.deviceForm.reset();
            // Adicione aqui a lógica para atualizar a lista de dispositivos, se necessário
          },
          error: () => Swal.fire('Erro', 'Erro ao registrar o dispositivo!', 'error')
        });
      }
    } else {
      Swal.fire('Atenção', 'Por favor, preencha todos os campos corretamente.', 'info');
    }
  }
}
<div class="card shadow-sm p-4 mt-5">
  <div class="card-body">
    <h3 class="title b-4">Registrar um novo Device</h3>
    <hr class="mb-2">

  <form [formGroup]="deviceForm" (ngSubmit)="addDevice()" class="row g-3 align-items-end needs-validation mt-3" novalidate>
    <div class="col-md-3">
      <label for="name" class="form-label">Nome:</label>
      <input id="name" formControlName="name" type="text" class="form-control" required>
      <div class="invalid-feedback">
        Por favor, insira o nome do dispositivo.
      </div>
    </div>
    <div class="col-md-3">
      <label for="manufacturer" class="form-label">Fabricante:</label>
      <input id="manufacturer" formControlName="manufacturer" type="text" class="form-control" required>
      <div class="invalid-feedback">
        Por favor, insira o fabricante do dispositivo.
      </div>
    </div>
    <div class="col-md-3">
      <label for="description" class="form-label">Descrição:</label>
      <input id="description" formControlName="description" type="text" class="form-control" required>
      <div class="invalid-feedback">
        Por favor, insira uma descrição.
      </div>
    </div>
    <div class="col-md-3">
      <button type="submit" class="btn btn-primary">{{ editingDevice ? 'Atualizar' : 'Registrar' }} Dispositivo</button>
    </div>
  </form>
</div>
</div>
