import { Component, OnInit, Input, OnChanges, SimpleChanges } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { DeviceService } from 'src/app/services/device/device.service';
import { Device } from 'src/app/models/device.model';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-device-register',
  templateUrl: './device-register.component.html',
  styleUrls: ['./device-register.component.scss']
})
export class DeviceRegisterComponent implements OnInit, OnChanges {
  deviceForm: FormGroup;
  @Input() device!: Device; 
  changeText = 'Registrar';


  constructor(
    private fb: FormBuilder,
    private deviceService: DeviceService
  ) {
    console.log(this.device);
    this.deviceForm = this.fb.group({
      id: [null],
      name: ['', Validators.required],
      description: ['', Validators.required],
      manufacturer: ['', Validators.required]
    });
    if(this.device != null){
      this.changeText = 'Registrar';

    }
    else{
      this.changeText = 'Editar';    }

  }

  ngOnInit(): void {
  }

  ngOnChanges(changes: SimpleChanges): void {
    if (changes['editingDevice'] && changes['editingDevice'].currentValue) {
      this.deviceForm.patchValue(changes['editingDevice'].currentValue);
    }
  }

  submitForm(): void {
    if (this.deviceForm.valid) {
      const deviceData = this.deviceForm.value;

      if (this.device) {
        this.editing = true
        // Atualiza o dispositivo existente
        this.deviceService.updateDevice(deviceData.id).subscribe({
          next: () => {
            Swal.fire('Atualizado', 'Dispositivo atualizado com sucesso!', 'success');
            this.deviceForm.reset();

          },
          error: () => Swal.fire('Erro', 'Não foi possível atualizar o dispositivo.', 'error')
        });
      } else {
        // Adiciona um novo dispositivo
        this.editing = false

        this.deviceService.addDevice(deviceData).subscribe({
          next: () => {
            Swal.fire('Registrado', 'Dispositivo registrado com sucesso!', 'success');
            this.deviceForm.reset();
          },
          error: () => Swal.fire('Erro', 'Não foi possível registrar o dispositivo.', 'error')
        });
      }
    } else {
      Swal.fire('Erro', 'Por favor, preencha todos os campos corretamente.', 'error');
    }
  }
}
<div class="card shadow-sm p-4 mt-5">
  <div class="card-body">
    <h3 class="title b-4">{{ editing ? 'Editar' : 'Registrar' }} um novo Dispositivo</h3>
    <hr class="mb-2">

    <form [formGroup]="deviceForm" (ngSubmit)="submitForm()" class="row g-3 align-items-end needs-validation mt-3" novalidate>
      <div class="col-md-3">
        <label for="name" class="form-label">Nome:</label>
        <input id="name" formControlName="name" type="text" class="form-control" required>
        <div class="invalid-feedback">
          Por favor, insira o nome do dispositivo.
        </div>
      </div>
      <div class="col-md-3">
        <label for="manufacturer" class="form-label">Fabricante:</label>
        <input id="manufacturer" formControlName="manufacturer" type="text" class="form-control" required>
        <div class="invalid-feedback">
          Por favor, insira o fabricante do dispositivo.
        </div>
      </div>
      <div class="col-md-3">
        <label for="description" class="form-label">Descrição:</label>
        <input id="description" formControlName="description" type="text" class="form-control" required>
        <div class="invalid-feedback">
          Por favor, insira uma descrição.
        </div>
      </div>
      <div class="col-md-3">
        <button type="submit" class="btn btn-primary">{{ editing ? 'Atualizar' : 'Registrar' }} Dispositivo</button>
      </div>
    </form>
  </div>
</div>
